import React, { useState, useEffect } from 'react';
import UrlForm from './components/UrlForm';
import UrlPreview from './components/UrlPreview';
import ThemeToggle from './components/ThemeToggle';
import Toast from './components/Toast';

export interface UrlData {
  url: string;
  domain: string;
  favicon?: string;
  title?: string;
  description?: string;
  image?: string;
}

export interface ToastMessage {
  id: string;
  message: string;
  type: 'success' | 'error' | 'info';
}

function App() {
  const [isDark, setIsDark] = useState(false);
  const [selectedUrl, setSelectedUrl] = useState<UrlData | null>(null);
  const [isLocked, setIsLocked] = useState(false);
  const [toasts, setToasts] = useState<ToastMessage[]>([]);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    const shouldUseDark = savedTheme === 'dark' || (!savedTheme && prefersDark);

    setIsDark(shouldUseDark);
    document.documentElement.classList.toggle('dark', shouldUseDark);
  }, []);

  const toggleTheme = () => {
    const newIsDark = !isDark;
    setIsDark(newIsDark);
    localStorage.setItem('theme', newIsDark ? 'dark' : 'light');
    document.documentElement.classList.toggle('dark', newIsDark);
  };

  const handleUrlSubmit = (urlData: UrlData) => {
    setSelectedUrl(urlData);
    setIsLocked(true);
    console.log('FINAL_URL', urlData.url);
  };

  const handleChangeUrl = () => {
    setIsLocked(false);
    setSelectedUrl(null);
  };

  const addToast = (message: string, type: ToastMessage['type'] = 'info') => {
    const id = Date.now().toString();
    setToasts(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setToasts(prev => prev.filter(toast => toast.id !== id));
    }, 3000);
  };

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors">
      <div className="container mx-auto px-4 py-8 sm:py-16">
        <div className="max-w-lg mx-auto">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="flex items-center justify-between mb-4">
              <div className="flex-1" />
              <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 dark:text-white">
                URL Collector
              </h1>
              <div className="flex-1 flex justify-end">
                <ThemeToggle isDark={isDark} onToggle={toggleTheme} />
              </div>
            </div>
            <p className="text-gray-600 dark:text-gray-400">
              Enter a URL to validate and preview
            </p>
          </div>

          {/* Main Card */}
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 sm:p-10">
            <UrlForm
              onSubmit={handleUrlSubmit}
              isLocked={isLocked}
              onChangeUrl={handleChangeUrl}
              onToast={addToast}
              initialUrl={selectedUrl?.url}
            />

            {selectedUrl && (
              <div className="mt-6 pt-6 border-t border-gray-200 dark:border-gray-700">
                <UrlPreview urlData={selectedUrl} onToast={addToast} />
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="text-center mt-8 text-sm text-gray-500 dark:text-gray-400">
            <p>Enter any valid URL starting with http:// or https://</p>
            <p className="mt-1">Press Enter to submit or use the buttons provided</p>
          </div>
        </div>
      </div>

      {/* Toast Container */}
      <div className="fixed bottom-4 right-4 z-50 space-y-2">
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            message={toast.message}
            type={toast.type}
            onClose={() => removeToast(toast.id)}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
